def hsv_to_rgb(h, s, v, a) -> tuple:
    """Convert HSV to RGB."""

    a = int(255*a)
    if s:
        if h == 1.0:
            h = 0.0
        i = int(h*6.0)
        f = h*6.0 - i

        w = int(255*(v * (1.0 - s)))
        q = int(255*(v * (1.0 - s * f)))
        t = int(255*(v * (1.0 - s * (1.0 - f))))
        v = int(255*v)

        if i == 0:
            return (v, t, w, a)
        if i == 1:
            return (q, v, w, a)
        if i == 2:
            return (w, v, t, a)
        if i == 3:
            return (w, q, v, a)
        if i == 4:
            return (t, w, v, a)
        if i == 5:
            return (v, w, q, a)
    else:
        v = int(255*v)
        return (v, v, v, a)


def temperature_color(temperature):
    """Map the temperature."""

    hue = 30 + 240 * (30 - temperature) / 60
    rgb = hsv_to_rgb(hue / 360.0, 1.0, 1.0, 1.0)
    return f'#{rgb[0]:02x}{rgb[1]:02x}{rgb[2]:02x}'


def format_temperature(temperature):
    """Format the temperature."""
    return ("+" if temperature >=
            0 else "") + f'{temperature:.0f}' + "°"


def safe_cast(val, to_type, default=None):
    """Safe casting."""
    try:
        return to_type(val)
    except (ValueError, TypeError):
        return default


def merge_custom_data(payload, data):
    """Merge the custom values."""
    return (payload | (data or {})) if payload else payload

def average(lst):
    """Average list values."""
    return sum(lst) / len(lst)

class AwtrixTime:
    """Allows to send updated to applications."""

    def __init__(self,
                 hass,
                 entity_id
                 ) -> None:
        """Initialize the device."""

        self.hass = hass
        self.entity_id = entity_id

    async def push_app_data(self, data):
        """Update the application data."""

        state = self.hass.states.get(self.entity_id)
        if state is not None and state.state is not None:
            if state.state == STATE_UNAVAILABLE:
                return

            if "name" not in data:
                raise Unresolvable("No app name")
            app_id = data["name"]
            topic = state.state + "/custom/" + app_id

            data = data.get("data", {}) or {}
            msg = data.copy()

            if 'icon' in msg:
                if str(msg["icon"]).startswith(('http://', 'https://')):
                    icon = await self.hass.async_add_executor_job(getIcon, str(msg["icon"]))
                    if icon:
                        msg["icon"] = icon

            payload = json.dumps(msg) if len(msg) else ""
            service_data = {"payload": payload,
                            "topic": topic}

            return await self.hass.services.async_call(
                "mqtt", "publish", service_data
            )

    async def switch_app(self, data):
        """Call API switch app."""

        state = self.hass.states.get(self.entity_id)
        if state is not None and state.state is not None:
            if state.state == STATE_UNAVAILABLE:
                return

            topic = state.state + "/switch"
            if "name" not in data:
                raise Unresolvable("No app name")
            app_id = data["name"]

            payload = json.dumps({"name": app_id})
            service_data = {"payload": payload,
                            "topic": topic}

            return await self.hass.services.async_call(
                "mqtt", "publish", service_data
            )

    async def settings(self, data):
        """Call API settings."""

        state = self.hass.states.get(self.entity_id)
        if state is not None and state.state is not None:
            if state.state == STATE_UNAVAILABLE:
                return

            topic = state.state + "/settings"
            data = data or {}
            msg = data.copy()

            payload = json.dumps(msg)
            service_data = {"payload": payload,
                            "topic": topic}

            return await self.hass.services.async_call(
                "mqtt", "publish", service_data
            )

    def weather_current(self, weather, temperature, hourly):
        """Get Weather current app."""
        try:
            forecast = []
            offset = 8
            if hourly:
                for idx, x in enumerate(hourly):
                    forecast.append(
                        {"dp": [idx + offset, 7, temperature_color(x['temperature'])]})
                    if idx + offset > 32:
                        break

            forecast.append({"dt": [16, 1, format_temperature(
                temperature), temperature_color(temperature)]})
            icon = icons.get(weather)
            payload = {"draw":
                       forecast,
                       "icon": icon,
                       "duration": 5,
                       "pushIcon": 2,
                       "lifetime": 900,
                       "weather": weather,
                       "repeat": 1
                       }
            return payload
        except Exception:
            return {}

    def weather_forecast(self, hourly, kind = "line"):
        """Get Weather forecast app."""

        temperature_array = []
        forecast_array = []

        for idx, x in enumerate(hourly):
            if idx < 11:
                temperature_array.append(round(x['temperature']))
                forecast_array.append(x['condition'])

        t_max = max(temperature_array)
        t_min = min(temperature_array)
        t_avg = average(temperature_array)
        t_delta = t_max - t_min
        for idx, t in enumerate(temperature_array):
            temperature_array[idx] = round((((t - t_min) * 7) / t_delta) + 1)

        icon = icons.get(forecast_array[len(forecast_array)-1])
        payload = {kind:
                   temperature_array,
                   "color": temperature_color(t_avg),
                   "icon": icon,
                   "duration": 5,
                   "pushIcon": 2,
                   "autoscale": False,
                   "lifetime": 900,
                   "repeat": 1
                   }
        return payload

    def weather_night(self, moon, temperature):
        """Get Weather night app."""

        try:
            moon = moon or "full_moon"
            icon = icons.get(moon)
            payload = {
                "icon": icon,
                "text":  format_temperature(temperature),
                "color": temperature_color(temperature),
                "lifetime": 900,
                "repeat": 1
            }
            return payload
        except Exception:
            return {}

    def weather_home(self, temperature):
        """Get Home climate app."""

        try:
            icon = icons.get("home")
            payload = {
                "icon": icon,
                "text":  format_temperature(temperature),
                "color": temperature_color(temperature),
                "lifetime": 900,
                "repeat": 1
            }
            return payload
        except Exception:
            return {}

    def weather_sun(self, next_rising, next_setting, sun_event_minute_threshold=30):
        """Get sun rise/set app."""

        try:
            payload = {
            }

            now = dt_util.as_local(dt_util.now())
            rising_local = dt_util.as_local(
                datetime.fromisoformat(next_rising))
            rising_str = rising_local.time().strftime('%H:%M')
            delta = rising_local - now
            min = delta.total_seconds() / 60
            if min < sun_event_minute_threshold:
                icon = icons.get("sunrise")
                payload = {
                    "icon": icon,
                    "text":  rising_str,
                    "lifetime": 900,
                    "repeat": 1
                }

            setting_local = dt_util.as_local(
                datetime.fromisoformat(next_setting))
            setting_str = setting_local.time().strftime('%H:%M')
            delta = setting_local - now
            min = delta.total_seconds() / 60
            if min < sun_event_minute_threshold:
                icon = icons.get("sunset")
                payload = {
                    "icon": icon,
                    "text": setting_str,
                    "lifetime": 900,
                    "repeat": 1
                }

            return payload
        except Exception:
            return {}

    async def weather_app(self, data):
        """Call Weather app."""

        state = self.hass.states.get(self.entity_id)
        if state is not None and state.state is not None:
            if state.state == STATE_UNAVAILABLE:
                return

            topic = state.state + "/custom/"
            custom_icons = data.get("icons")
            for icon in custom_icons:
                icons[icon] = custom_icons[icon]

            weather_entity = data['weather']
            outside_temperature_entity = data.get('outside_temperature')

            weather = self.hass.states.get(weather_entity)
            temperature = weather.attributes.get("temperature")

            if outside_temperature_entity is not None:
                outside_temperature = self.hass.states.get(
                    outside_temperature_entity)
                if outside_temperature is not None:
                    outside_temperature_value = safe_cast(
                        outside_temperature.state, float)
                    if outside_temperature_value is not None:
                        temperature = outside_temperature_value

            templow = temperature
            forecast = weather.attributes.get("forecast")
            if not forecast:
                templow = temperature
            else:
                templow = forecast[0].get("templow")

            hourly = []
            try:
                hourly_response = await self.hass.services.async_call(
                    "weather", "get_forecasts",
                    {'entity_id': weather_entity, 'type': 'hourly'},
                    blocking=True,
                    return_response=True
                )
                hourly = hourly_response[weather_entity].get('forecast')
                night_temperature = None
                for d in hourly:
                    dt = (datetime.fromisoformat(
                        d['datetime'])).replace(tzinfo=None)
                    if dt.hour >= 0 and dt.hour <= 6:
                        night_temperature = d['temperature'] if night_temperature is None else min(
                            night_temperature, d['temperature'])
                        # print(d)
                    elif night_temperature:
                        break

                templow = templow if night_temperature is None else night_temperature
            except Exception:
                pass

            customize = data.get('data') or {}

            # current
            payload = self.weather_current(weather.state, temperature, hourly)
            payload = merge_custom_data(payload, customize.get('weather'))

            payload = json.dumps(payload)
            service_data = {"payload": payload,
                            "topic": topic + "weather"}

            await self.hass.services.async_call(
                "mqtt", "publish", service_data
            )

            # Forecast
            fc_graph = data.get('forcast_graph')
            if fc_graph and hourly:
                payload = self.weather_forecast(hourly, fc_graph)
                payload = merge_custom_data(
                    payload, customize.get('weather_forecast'))

                payload = json.dumps(payload)
                service_data = {"payload": payload,
                                "topic": topic + "weather_forecast"}

                await self.hass.services.async_call(
                    "mqtt", "publish", service_data
                )

            # Night
            moon_entity = data.get('moon')
            if moon_entity:
                moon = self.hass.states.get(moon_entity)
                if moon is not None:
                    moon_phase = moon.state
                    payload = self.weather_night(moon_phase, templow)
                    payload = merge_custom_data(
                        payload, customize.get('weather_night'))

                    payload = json.dumps(payload)
                    service_data = {"payload": payload,
                                    "topic": topic + "weather_night"}

                    await self.hass.services.async_call(
                        "mqtt", "publish", service_data
                    )

            # custom
            frames = data.get("frames")
            for frame in frames:
                frame_data = frames.get(frame)
                payload = {}
                for item in frame_data or []:
                    payload[item] = frame_data[item]

                if payload:
                    payload = merge_custom_data(
                        {"lifetime": 900, "repeat": 1}, payload)
                payload = json.dumps(payload)
                service_data = {"payload": payload,
                                "topic": topic + "weather_" + frame}

                await self.hass.services.async_call(
                    "mqtt", "publish", service_data
                )

            # home
            home_temperature_entity = data.get('home_temperature')
            if home_temperature_entity:
                home_temperature = self.hass.states.get(
                    home_temperature_entity)
                if home_temperature is not None:
                    home_temperature_value = safe_cast(
                        home_temperature.state, float)
                    if home_temperature_value is not None:
                        payload = self.weather_home(home_temperature_value)
                        payload = merge_custom_data(
                            payload, customize.get('weather_home'))
                        payload = json.dumps(payload)
                        service_data = {"payload": payload,
                                        "topic": topic + "weather_home"}

                        await self.hass.services.async_call(
                            "mqtt", "publish", service_data
                        )

            # Sun
            sun_event_minute_threshold = customize.get(
                'sun_event_minute_threshold') or 30
            sun_entity = data.get('sun')
            if sun_entity:
                sun = self.hass.states.get(sun_entity)
                if sun is not None:
                    payload = self.weather_sun(sun.attributes.get(
                        'next_rising'), sun.attributes.get('next_setting'), sun_event_minute_threshold)
                    payload = merge_custom_data(
                        payload, customize.get('weather_sun'))
                    payload = json.dumps(payload)
                    service_data = {"payload": payload,
                                    "topic": topic + "weather_sun"}

                    await self.hass.services.async_call(
                        "mqtt", "publish", service_data
                    )

            return True

    async def rtttl(self, data):
        """Play rtttl."""

        state = self.hass.states.get(self.entity_id)
        if state is not None and state.state is not None:
            if state.state == STATE_UNAVAILABLE:
                return

            topic = state.state + "/rtttl"
            if "rtttl" not in data:
                raise Unresolvable("No rtttl.")

            rtttl_text = data["rtttl"]
            service_data = {"payload": rtttl_text,
                            "topic": topic}

            return await self.hass.services.async_call(
                "mqtt", "publish", service_data
            )

    async def sound(self, data):
        """Play rtttl sound."""

        state = self.hass.states.get(self.entity_id)
        if state is not None and state.state is not None:
            if state.state == STATE_UNAVAILABLE:
                return

            topic = state.state + "/sound"
            if "sound" not in data:
                raise Unresolvable("No sound name")

            sound_id = data["sound"]
            payload = json.dumps({"sound": sound_id})
            service_data = {"payload": payload,
                            "topic": topic}

            return await self.hass.services.async_call(
                "mqtt", "publish", service_data
            )

def getIcon(url):
    """Get icon by url."""
    try:
        timeout=5
        response = requests.get(url, timeout=timeout)
        if response and response.status_code == 200:
            pil_im = Image.open(BytesIO(response.content))
            pil_im = pil_im.convert('RGB')
            b = BytesIO()
            pil_im.save(b, 'jpeg')
            im_bytes = b.getvalue()
            return base64.b64encode(im_bytes).decode()
    except Exception:
        pass
